plugin.phpcode:
global $vbulletin;
if ($vbulletin->options['red_group_icons_enabled'] && !empty($post['userid'])){
	$query = "SELECT sgm.userid,sgm.groupid,sg.name,sgi.dateline,sgi.extension FROM " . TABLE_PREFIX . "socialgroupmember sgm LEFT JOIN " . TABLE_PREFIX . "socialgroup sg ON sg.groupid = sgm.groupid LEFT JOIN " . TABLE_PREFIX ."socialgroupicon sgi ON sgi.groupid = sgm.groupid WHERE sgm.userid = {$post['userid']} AND sg.type = 'public'";
	$sg = $GLOBALS['vbulletin']->db->query($query);
	$icons = array();
	while($row = $GLOBALS['vbulletin']->db->fetch_array($sg)){
		if (is_array($row)){
		    if ($vbulletin->options['usefilegroupicon']){
		        $row['image_url'] = $vbulletin->options['groupiconurl'] . '/thumbs/socialgroupicon_' . $row['groupid'] . '_' . $row['dateline'] . '.gif';
		    }else{
		        $row['image_url'] = 'image.php?groupid=' . $row['groupid'] . '&dateline=' . $row['dateline'] . '&type=groupthumb';
		    }
			$icons[$row['groupid']] = $row;
		}
	}
	if (count($icons) > 0){
		$templater = vB_Template::create('red_social_group_icons');
		$templater->register('red_social_icons', $icons);
		$template_hook['postbit_userinfo_right'] .= $templater->render();
	}
}
